  }

  .nav-group {
    width: 235px;
    height: 226px;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    margin-top: 40px;

    a {
      text-decoration: none;
    }

    .main,
    .profile,
    .setting,
    .notification,
    .public-chat-room,
    .private-message {
      display: flex;
      margin: 10px;
    }
    .tweet {
